
public class ternaryoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 9;
        int b = 0b101010011010;
        String c = (2>=1?"yes":"no");
        String d = (a>=(byte)b?"true":"false");       
        System.out.println((byte)b);
        System.out.println(d);
        
        System.out.print(c);
        
	}

}
